import React, { useState } from 'react';
import './Booking.css';

function Booking() {
  const [form, setForm] = useState({
    name: '',
    people: '',
    table: '1',
    time: '',
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Table booked!\nName: ${form.name}\nPeople: ${form.people}\nTable: ${form.table}\nTime: ${form.time}`);
    // Optionally send this data to backend here
  };

  return (
    <div className="booking-container">
      <h2>Book a Table</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" value={form.name} onChange={handleChange} required />

        <label>Number of People:</label>
        <input type="number" name="people" value={form.people} onChange={handleChange} min="1" required />

        <label>Table Number (1-8):</label>
        <select name="table" value={form.table} onChange={handleChange}>
          {[...Array(8)].map((_, i) => (
            <option key={i} value={i + 1}>{i + 1}</option>
          ))}
        </select>

        <label>Time:</label>
        <input type="time" name="time" value={form.time} onChange={handleChange} required />

        <button type="submit">Book</button>
      </form>
    </div>
  );
}

export default Booking;
