import { useState } from 'react';
import "./Booking.css";

const Booking = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    date: '',
    time: '',
    guests: 1,
    specialRequests: ''
  });

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission
    console.log("Form Data being sent:", formData);  // Debug: log form data
    
    try {
      const response = await fetch('http://localhost:5000/api/bookings', { // Ensure the URL is correct
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log("Response Status:", response.status);  // Debug: log response status
      const data = await response.json();
      console.log("Response Data:", data);  // Debug: log response data

      if (response.ok) {
        alert(data.message); // Show success alert
      } else {
        alert(data.message); // Show failure alert
      }
    } catch (error) {
      console.error("Booking Error:", error);  // Log any error that occurs during fetch
    }
  };

  return (
    <div className="booking-container">
      <h2>Book your table now</h2>  {/* Added the title */}
      <form onSubmit={handleSubmit} className="booking-form">
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Your Name"
          required
        />
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Your Email"
          required
        />
        <input
          type="tel"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          placeholder="Your Phone"
          required
        />
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />
        <input
          type="time"
          name="time"
          value={formData.time}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="guests"
          value={formData.guests}
          onChange={handleChange}
          min="1"
          required
        />
        <textarea
          name="specialRequests"
          value={formData.specialRequests}
          onChange={handleChange}
          placeholder="Any Special Requests?"
        />
        <button type="submit">Book Now</button>
      </form>
    </div>
  );
};

export default Booking;
